changeSet.sql:
-- Update existing tenderId field with correct regex pattern UPDATE document_type_fields SET ocr_pattern = 'Tender/Proposal ID\s*:\s*([0-9]+)', is_ocr_mappable = true, description = 'Tender/Proposal ID extracted from OCR text' WHERE document_type = 'TENDER_NOTICE' AND field_key = 'tenderId';
-- Update existing tenderDate field with publication date pattern UPDATE document_type_fields SET ocr_pattern = '(?:Scheduled\s+)?(?:Tender|Proposal)\s+(?:Document\s+)?(?:last\s+selling\s*/\s*downloading|Publication|last\s+selling|downloading)\s+(?:Date\s+and\s+Time|Date)\s*:\s*(\d{1,2}[\-/]\w+[\-/]\d{4}\s+\d{1,2}:\d{2})', description = 'Tender publication/scheduled date (when tender was published)' WHERE document_type = 'TENDER_NOTICE' AND field_key = 'tenderDate';
-- Update existing closingDate field with correct regex pattern UPDATE document_type_fields SET ocr_pattern = '(?:Tender|Proposal)\s+(?:Closing|Submission)\s+(?:Date\s+and\s+Time|Date)\s*:\s*(\d{1,2}[\-/]\w+[\-/]\d{4}\s+\d{1,2}:\d{2})', description = 'Tender closing date (last date for submission)' WHERE document_type = 'TENDER_NOTICE' AND field_key = 'closingDate';
